import React, { Component } from "react";
import Chart from "react-apexcharts";
import ReactApexCharts from "react-apexcharts";
import ApexCharts from "react-apexcharts";

class Column extends Component {
  state = {
    values: this.props.values
  };

  handleChange = () => {
    this.setState({
      series: {
        ...this.state.values.series
      }
    });
    ApexCharts.exec("basic-bar", "updateSeries", [
      {
        data: this.state.values.series
      }
    ]);
  };

  render() {
    console.log("rendering column", this.state.values);
    return (
      <ReactApexCharts
        options={this.state.values.options}
        series={this.state.values.series}
        onClick={() => this.handleChange()}
        type="bar"
        width="500"
      />
    );
  }
}

export default Column;
