import React, { Component } from "react";
import Buttons from "../components/buttons";
import Notification from "../components/Notification";

class form extends Component {
  state = {
    option: this.props.option,
    series: this.props.series,
    parameters: [],
    values: [],
    CategoryInput: "",
    ParamsInput: ""
  };

  handleSubmitCat = val => {
    this.setState({ CategoryInput: val });
  };
  handleSubmitPar = val => {
    this.setState({ ParamsInput: val });
  };
  render() {
    return (
      <div className="dashboard container">
        <div className="row">
          <div className="col s12 m6">
            <Buttons onSubmit={this.handleSubmitCat} name="Category" />
            <Buttons onSubmit={this.handleSubmitPar} name="Parameter" />
          </div>
          <div className="col s12 m5 offset-m1">
            <Notification value={this.state.CategoryInput} />
          </div>
        </div>
      </div>
    );
  }
}

export default form;
