import React, { Component } from "react";
import Donut from "../src/components/Donut";
import Graph from "../src/components/graph";
import Column from "../src/components/Column";

class App extends Component {
  state2 = {
    options: {},
    series: [44, 55, 41, 17, 15],
    labels: ["A", "B", "C", "D", "E"]
  };

  state = {
    options: {
      chart: {
        id: "basic-bar"
      },
      xaxis: {
        categories: ["א", "ב", "ג", "ד", "ה", "ו", "ז"]
      }
    },
    series: [
      {
        name: "series-1",
        data: [30, 40, 45, 50, 49, 60, 70]
      }
    ],
    arr: [],
    inputText: ""
  };

  handleChange(event) {
    console.log(event.target);
    //this.setState({ inputText: event.target.value });
  }

  onIncrement = number => {
    console.log("clicked");
    const state_copy = [...this.state.arr];
    state_copy.push(number);
    console.log(state_copy);
    this.setState({ arr: state_copy });
  };

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Column values={this.state} />
            <label>
              Name:
              <input
                type="text"
                value={this.state.value}
                onChange={() => this.handleChange()}
              />
            </label>
            <button onClick={() => this.onIncrement(1)}>Submit</button>
            <Graph values={this.state} />
            <div className="donut">
              <Donut values={this.state2} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
