import React, { Component } from "react";
import Buttons from "../components/buttons";
import Notification from "../components/Notification";

class form extends Component {
  state = {
    option: this.props.option,
    series: this.props.series,
    parameters: [],
    values: [],
    CategoryInput: "",
    ParamsInput: ""
  };

  handleSubmitCat = val => {
    const CategoryInputCopy = [...this.state.CategoryInput];
    CategoryInputCopy.push(val);
    this.setState({ CategoryInput: CategoryInputCopy });
  };
  handleSubmitPar = val => {
    const ParamsInputCopy = [...this.state.ParamsInput];
    ParamsInputCopy.push(val);
    this.setState({ ParamsInput: ParamsInputCopy });
  };
  render() {
    return (
      <div className="dashboard container">
        <div className="row">
          <div className="col s12 m6">
            <Buttons onSubmit={this.handleSubmitCat} name="Category" />
            <Buttons onSubmit={this.handleSubmitPar} name="Parameter" />
          </div>
          <div className="col s12 m5 offset-m1">
            <Notification values={this.state.CategoryInput} name={"Category"} />
            <Notification values={this.state.ParamsInput} name={"Parameters"} />
          </div>
        </div>
      </div>
    );
  }
}

export default form;
