import React, { Component } from "react";
import Donut from "../src/components/Donut";
import Graph from "../src/components/graph";
import Column from "../src/components/Column";
import Chart from "react-apexcharts";
import ReactApexChart from "react-apexcharts";

class App extends Component {
  constructor(props) {
    super(props);
    this.state2 = {
      options: {},
      series: [44, 55, 41, 17, 15],
      labels: ["A", "B", "C", "D", "E"]
    };

    this.state = {
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: ["א", "ב", "ג", "ד", "ה", "ו", "ז"]
        }
      },
      series: [
        {
          name: "series-1",
          data: [10]
        }
      ],
      num: 0,
      inputText: ""
    };
  }
  handleChange = e => {
    this.setState({
      inputText: e.target.value
    });
  };

  getBadgeClasses() {
    if (this.state.num == 0) {
      return;
    } else {
      return <Column values={this.state} />;
    }
  }

  onIncrement = () => {
    const state_copy = Object.assign({}, this.state);
    state_copy.series["0"]["data"].push(parseInt(this.state.inputText));
    this.setState({ inputText: "" });
    this.setState({
      series: state_copy.series
    });
    this.setState({ num: 1 });
  };

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">{this.getBadgeClasses()}</div>
        </div>
      </div>
    );
  }
}

export default App;
