import React, { Component } from "react";
import Donut from "../src/components/Donut";
import Graph from "../src/components/graph";
import Column from "../src/components/Column";
import Chart from "react-apexcharts";

class App extends Component {
  constructor(props) {
    super(props);
    this.state2 = {
      options: {},
      series: [44, 55, 41, 17, 15],
      labels: ["A", "B", "C", "D", "E"]
    };

    this.state = {
      options: {
        chart: {
          id: "basic-bar"
        },
        xaxis: {
          categories: ["א", "ב", "ג", "ד", "ה", "ו", "ז"]
        }
      },
      series: [
        {
          name: "series-1",
          data: [30, 40, 45, 50, 49, 60, 70]
        }
      ],
      arr: [],
      inputText: ""
    };
  }
  handleChange = e => {
    this.setState({
      inputText: e.target.value
    });
  };
  onIncrement = () => {
    const state_copy = Object.assign({}, this.state);
    state_copy.series["0"]["data"].push(parseInt(this.state.inputText));
    this.setState({ inputText: "" });
    this.setState({
      series: state_copy.series
    });
    this.render();
    //this.setState({ series: state_copy.series });
    console.log("onIncrement", this.state.series);
  };

  render() {
    return (
      <div className="app">
        <div className="row">
          <div className="mixed-chart">
            <Chart
              options={this.state.options}
              series={this.state.series}
              type="bar"
              width="500"
            />
            <label>
              Name:
              <input
                type="text"
                value={this.state.inputText}
                onChange={this.handleChange}
              />
            </label>
            <button onClick={() => this.onIncrement()}>Submit</button>
            <Graph values={this.state} />
            <div className="donut">
              <Donut values={this.state2} />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
