import React, { Component } from "react";
import ReactApexChart from "react-apexcharts";
import ReactFileReader from "react-file-reader";

import text from "./israel.txt";

class SplineChart extends Component {
  constructor(props) {
    super(props);

    this.state = {
      data: [],

      series: [
        {
          name: "series1",
          data: [3824, 4316, 4782, 5523, 6168, 6857, 7120],
        },
      ],
      options: {
        chart: {
          height: 350,
          type: "area",
        },
        dataLabels: {
          enabled: false,
        },
        stroke: {
          curve: "smooth",
        },
        xaxis: {
          type: "datetime",
          categories: [
            "2020-03-27",
            "2020-03-28",
            "2020-03-30",
            "2020-03-31",
            "2020-04-01",
            "2020-04-02",
            "2020-04-03",
          ],
        },
        tooltip: {
          x: {
            format: "dd/MM/yy",
          },
        },
      },
    };
  }

  handleFetch() {
    console.log("handle Fetch");
    fetch("http://127.0.0.1:3001/api/is")
      .then((data) => data.json())
      .then((res) => this.setState({ data: res.data.split("[") }))
      .then((res) => console.log(this.state.data[1].split(" ")[1].toInt()));
  }
  componentDidMount() {
    this.handleFetch();
  }

  render() {
    return (
      <div id="chart">
        {this.state.data.length}
        {/* {this.state.data.map((d) => (
          <p>{d.split(" ")[0]}</p>
        ))} */}
        {/* {this.state.data[0]}
        {this.state.data[5]}
        {this.state.data[10]}
        {this.state.data[15]} */}

        <ReactApexChart
          options={this.state.options}
          series={this.state.series}
          type="area"
          height={300}
        />
      </div>
    );
  }
}
export default SplineChart;
