import React, { Component } from "react";
import ReactApexChart from "react-apexcharts";
import ReactFileReader from "react-file-reader";

import text from "../israel.txt";
import fs from "fs";

class SplineChart extends Component {
  constructor(props) {
    super(props);

    var textByLine = fs.readFileSync(text).toString().split("\n");
    console.log(textByLine);

    this.state = {
      series: [
        {
          name: "series1",
          data: [3824, 4316, 4782, 5523, 6168, 6857, 7120],
        },
      ],
      options: {
        chart: {
          height: 350,
          type: "area",
        },
        dataLabels: {
          enabled: false,
        },
        stroke: {
          curve: "smooth",
        },
        xaxis: {
          type: "datetime",
          categories: [
            "2020-03-27",
            "2020-03-28",
            "2020-03-30",
            "2020-03-31",
            "2020-04-01",
            "2020-04-02",
            "2020-04-03",
          ],
        },
        tooltip: {
          x: {
            format: "dd/MM/yy",
          },
        },
      },
    };
  }

  render() {
    let newText = text.split("\n").map((item, i) => {
      return <p key={i}>{item}</p>;
    });
    return (
      <div id="chart">
        {newText}
        <ReactApexChart
          options={this.state.options}
          series={this.state.series}
          type="area"
          height={300}
        />
      </div>
    );
  }
}
export default SplineChart;
